package Day1;

public class DemoSwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day = 2;
		
		switch (day) {
		case 1:
			System.out.println("Mday");
			
			break;
		case 2:
			System.out.println("Tday");
			
			break;
		case 3:
			System.out.println("Wday");
			
			break;

		default:
			System.out.println("Incorrect day");
			break;
		}

	}

}
